var link=document.querySelector(".searchtitle a"),flag=0,form=document.querySelector(".search__form");link.addEventListener("click",function(a){0==flag?(a.preventDefault(),form.classList.add("search__form--show"),flag+=1):(a.preventDefault(),form.classList.remove("search__form--show"),flag=0)}),link.addEventListener("keydown",function(a){27===a.keyCode&&form.classList.contains("search__form--show")&&(form.classList.remove("search__form--show"),flag=0)});
